import React from "react";
import { Image, StyleSheet, Text, View, ScrollView } from "react-native";

const MusicItem = ({ item }) => {
  state = {
    results: [],
    searchQuery: "",
    isLoading: false
}
  return (
    <View style={styles.container}>
      <Image style={styles.image} source={{ uri: item.artwork }} />
      <View>
        <ScrollView style={styles.info}>
        <ScrollView style={styles.resultsScroll}>
                    {
                        this.state.results.map((result, index) => (
                            <ListItem
                                key={index}
                                leftAvatar={{ source: { uri: result.artworkUrl100 } }}
                                title={result.artistName}
                                subtitle={result.trackName}
                                bottomDivider
                                subtitleStyle={{ color: "tomato" }}
                                onPress={() => { this.props.navigation.navigate('Result', { id: result.trackId }) }}
                                chevron
                            />
                        ))
                    }
                </ScrollView>

          <Text style={styles.title}>{item.title}</Text>
          <Text style={styles.details}>{item.artist}</Text>

        </ScrollView>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: { flexDirection: "row" },
  image: { width: 50, height: 50, margin: 5, borderRadius: 50 },
  info: { flex: 1, justifyContent: "center" },
  title: { fontSize: 20 },
  details: { color: "gray" },
});

export default MusicItem;
